mapper.select:
select

from user
where id = #{id,jdbcType=INTEGER}
  and account = #{account,jdbcType=VARCHAR}

mapper.delete:
delete from user
where id = #{id,jdbcType=INTEGER}
  and account = #{account,jdbcType=VARCHAR}

mapper.update:
update user


    password = #{password,jdbcType=VARCHAR},


    salt = #{salt,jdbcType=VARCHAR},


where id = #{id,jdbcType=INTEGER}
  and account = #{account,jdbcType=VARCHAR}

update user
set password = #{password,jdbcType=VARCHAR},
  salt = #{salt,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
  and account = #{account,jdbcType=VARCHAR}
